import { useRouteError } from 'react-router-dom';
import PageContent from '../components/PageContent.js';

function ErrorPage(){
    const error = useRouteError()
    let title,message;
    if (error.status === 404) {
        title = "404 Page Not Found";
        message = "Page or resources are not found or unable to load it.";
    }
    if(error.status === 201){
        title="201 Page is Broken";
        message ="Page is Broken or Some of its Content."
    }
    return(
        <>
        <PageContent  title={title}>
            <p>{message}</p>
        </PageContent>
        </>
    )
}

export default ErrorPage;
