import {  useLoaderData, json, redirect } from 'react-router-dom';
import EventItem from '../components/EventItem';

function EventsDetailsPage() {
    const data = useLoaderData();

    return (
        <>
            <EventItem event={data.event}/>
            {/* <p><Link to={".."} relative='path'>Go Back to Events Page</Link></p> */}
        </>
    )
}
export default EventsDetailsPage;


export async function loader({request,params}) {

    const id = params.id;   
    const respData = await fetch('http://localhost:8080/events/'+id);

    if (!respData.ok) {
        return json({ message: "Page not found!! " }, { status: 404 });
    } else {
        const response = await respData.json();
        return response;
    }
}

export async function action ({params,req}){
    const id = params.id;

    const response = await fetch(`http://localhost:8080/events/${id}`,{
        method: req.method,
    });

    if(!response.ok){
        throw json({
            message:"Something went wrong unable to delete Data",
            status:302,
        });
    }
    return redirect("events");
}
